import React from 'react';
import './Login.css';
import Formulario from '../Formulário/Formulario';
import login from '../Imagens/Gifs/Privacy-policy-unscreen.gif';
import axios from 'axios';
import { useState } from 'react';

function Login({ history }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  // login.js

function handleLogin(req, res) {
    const { email, password } = req.body;
    // Verifique as credenciais de login aqui
    if (email === 'user@gmail.com' && password === '12345') {
      // Credenciais válidas, gere um token de autenticação
      const token = generateAuthToken();
  
      // Retorne o token como resposta
      res.json({ token });
    } else {
      // Credenciais inválidas, retorne uma resposta de erro
      res.status(401).json({ error: 'Credenciais inválidas' });
    }
  }
  
  module.exports = handleLogin;  

  return (
    <div className='Login'>
      <div id='texto-img'>
        <p className='texto1'><b>Faça login</b></p>
        <p className='texto1'><b>E entre para o nosso time</b></p>
        <img src={login} id='figura-animada'/>
      </div>
      <div id='base-input'>
        <h1 id='tiulo'>Login</h1>
        <input className='campo-texto' type='email' placeholder='Digite seu email' value={email} onChange={handleEmailChange}></input>
        <input className='campo-texto' type='password' placeholder='Digite sua senha' value={password} onChange={handlePasswordChange}></input>
        <button id='confirmar' onClick={handleLogin}>Confirmar</button>
      </div>
    </div>
  );
}

export default Login;
