import './Login.css';
import { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Importe o hook useHistory para redirecionar a página

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory(); // Instancie o hook useHistory

  const handleSubmit = () => {
    if (email === 'usuario@example.com' && password === 'senha123') {
      // Credenciais válidas, redirecionar para outra página
      history.push('/outra-pagina');
    } else {
      // Credenciais inválidas, exibir mensagem de erro ou tomar outra ação apropriada
      console.log('Credenciais inválidas');
    }
  };

  return (
    <div className='Login'>
      <div id='texto-img'>
        <p className='texto1'><b>Faça login</b></p>
        <p className='texto1'><b>E entre para o nosso time</b></p>
        <img src={login} id='figura-animada'/>
      </div>
      <div id='base-input'>
        <h1 id='tiulo'>Login</h1>
        <input
          className='campo-texto'
          type='email'
          placeholder='Digite seu email'
          value={email} // Adicione o valor do estado email
          onChange={(e) => setEmail(e.target.value)} // Atualize o estado email ao digitar
        />
        <input
          className='campo-texto'
          type='password'
          placeholder='Digite sua senha'
          value={password} // Adicione o valor do estado password
          onChange={(e) => setPassword(e.target.value)} // Atualize o estado password ao digitar
        />
        <button id='confirmar' onClick={handleSubmit}>Confirmar</button>
      </div>
    </div>
  );
}

export default Login;
