import { Redirect } from 'react-router-dom';
import React, { useState } from 'react';
import './Login.css';
import Formulario from '../Formulário/Formulario';
import login from '../Imagens/Gifs/Privacy-policy-unscreen.gif';
import axios from 'axios';


function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [redirect, setRedirect] = useState(false);

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLogin = () => {
    const data = {
      email: email,
      password: password
    };

    axios.post('/api/login', data)
      .then((response) => {
        const token = response.data.token;
        setRedirect(true); // Ativa o redirecionamento
      })
      .catch((error) => {
        console.error(error);
      });
  };

  if (redirect) {
    return <Redirect to="/" />; // Redireciona para a página inicial
  }

  return (
    <div className='Login'>
      <div id='texto-img'>
        <p className='texto1'><b>Faça login</b></p>
        <p className='texto1'><b>E entre para o nosso time</b></p>
        <img src={login} id='figura-animada' alt='Figura animada' />
      </div>
      <div id='base-input'>
        <h1 id='tiulo'>Login</h1>
        <input className='campo-texto' type='email' placeholder='Digite seu email' value={email} onChange={handleEmailChange} />
        <input className='campo-texto' type='password' placeholder='Digite sua senha' value={password} onChange={handlePasswordChange} />
        <button id='confirmar' onClick={handleLogin}>Confirmar</button>
      </div>
    </div>
  );
}

export default Login;
