function Login(){

    function Confirmar() {
        const nodemailer = require('nodemailer');

        const transport = nodemailer.createTransport({
          host: 'smtp.gmail.com',
          port: 587,
          secure: false,
          auth: {
            user: 'mh16122006@gmail.com',
            pass: 'teteu15122006',
          }
        });

        const nome = document.getElementById('Nome').value;
        const unidade = document.getElementById('Unidade').value;
        const laboratorio = document.getElementById('Laboratorio').value;
        const ocorencia = document.getElementById('Ocorrencia').value;
        const maquina = document.getElementById('maquina').value;

        const mailOptions = {
          from: 'mh16122006@gmail.com',
          to: 'mh15122006@gmail.com',
          subject: `Olá, ${nome}!`,
          text: `Nome: ${nome}\nUnidade: ${unidade}\nLaboratório: ${laboratorio}\nOcorrência: ${ocorencia}\nMáquina: ${maquina}`
        };

        transport.sendMail(mailOptions, function (error, info) {
          if (error) {
            console.log(error);
          } else {
            console.log('Email enviado: ' + info.response);
          }
        });
      }

    return(
        <div>
            <h1>Login</h1>
            <input type="text" id="nome" placeholder="Digite o seu nome"/>
            <input type="password" id="senha" placeholder="Digite a sua senha"/>
        </div>
    );

}

export default Login;
