import "./Menu.css";
import React, { useState } from 'react';

const Menu = () => {
  const [showMenu, setShowMenu] = useState(false);

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  return (
    <nav className="menu">
      <div className="menu-icon" onClick={toggleMenu}>
        &#9776;
      </div>
      <div className={`menu-items ${showMenu ? 'show' : ''}`}>
        <a href="#">Página 1</a>
        <a href="#">Página 2</a>
        <a href="#">Página 3</a>
      </div>
    </nav>
  );
};

export default Menu;
