import './Login.css'
import Formulario from '../Formulário/Formulario';
import login from '../Imagens/Gifs/Privacy-policy-unscreen.gif'
import axios from 'axios';
import { useState } from 'react';
import { useHistory } from 'reac';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLogin = () => {
    const data = {
      email: email,
      password: password
    };

    axios.post('/api/login', data)
      .then((response) => {
        const token = response.data.token;
        // Armazenar o token em localStorage ou cookies, por exemplo
        // localStorage.setItem('token', token);
        // Redirecionar o usuário para a próxima página, se necessário
        history.push('/home');
      })
      .catch((error) => {
        // Lidar com erros de login, se necessário
        console.error(error);
      });
  };

  return (
    <div className='Login'>
      <div id='texto-img'>
        <p className='texto1'><b>Faça login</b></p>
        <p className='texto1'><b>E entre para o nosso time</b></p>
        <img src={login} id='figura-animada'/>
      </div>
      <div id='base-input'>
        <h1 id='tiulo'>Login</h1>
        <input className='campo-texto' type='email' placeholder='Digite seu email' value={email} onChange={handleEmailChange}></input>
        <input className='campo-texto' type='password' placeholder='Digite sua senha' value={password} onChange={handlePasswordChange}></input>
        <button id='confirmar' onClick={handleLogin}>Confirmar</button>
      </div>
    </div>
  );
}

export default Login;
