import "./Emprestimos.css";
import gif from "../Imagens/Imagens Site/Research paper.gif";
import React, { useState } from "react";

function Emprestimos() {
  const [quantidades, setQuantidades] = useState({
    lapis: 0,
    borracha: 0,
    apontador: 0,
    caneta: 0,
    canetao: 0,
    fitaDurex: 0,
    grampeador: 0,
  });

  const incrementarQuantidade = (item) => {
    if (quantidades[item] < 20) {
      setQuantidades((prevState) => ({
        ...prevState,
        [item]: prevState[item] + 1,
      }));
    }
  };

  const decrementarQuantidade = (item) => {
    if (quantidades[item] > 0) {
      setQuantidades((prevState) => ({
        ...prevState,
        [item]: prevState[item] - 1,
      }));
    }
  };

  const resetarValores = () => {
    setQuantidades({
      lapis: 0,
      borracha: 0,
      apontador: 0,
      caneta: 0,
      canetao: 0,
      fitaDurex: 0,
      grampeador: 0,
    });
    alert("Pedido enviado com sucesso!");
  };

  return (
    <div className="Emprestimos">
      <h1 id="title">Empréstimos de equipamentos</h1>
      <div className="base-site">
        <img src={gif} alt="Research paper" />
        <div className="base-itens">
          <div className="itens">
            <p>Lápis</p>
            <div className="quantidade">
              <button onClick={() => decrementarQuantidade("lapis")}>-</button>
              <span>{quantidades.lapis}</span>
              <button onClick={() => incrementarQuantidade("lapis")}>+</button>
            </div>
          </div>
          {/* Resto dos itens */}
        </div>
      </div>
      <button onClick={resetarValores}>Resetar Valores</button>
    </div>
  );
}

export default Emprestimos;
