import "./Emprestimos.css";
import gif from "../Imagens/Imagens Site/Research paper.gif";
import React, { useState } from "react";

function Emprestimos() {
  const [quantidade, setQuantidade] = useState(0);

  const incrementarQuantidade = () => {
    setQuantidade(quantidade + 1);
  };

  const decrementarQuantidade = () => {
    if (quantidade > 0) {
      setQuantidade(quantidade - 1);
    }
  };

  return (
    <div className="Emprestimos">
      <h1 id="title">Empréstimos de equipamentos</h1>
      <div className="base-site">
        <img src={gif} alt="Research paper" />
        <div className="base-itens">
          <div className="itens">
            <p>Lápis</p>
            <div className="quantidade">
              <button onClick={decrementarQuantidade}>-</button>
              <span>{quantidade}</span>
              <button onClick={incrementarQuantidade}>+</button>
            </div>
          </div>
          <div className="itens">
            <p>Borracha</p>
            <div className="quantidade">
              <button onClick={decrementarQuantidade}>-</button>
              <span>{quantidade}</span>
              <button onClick={incrementarQuantidade}>+</button>
            </div>

          </div>
        </div>
      </div>
    </div>
  );
}

export default Emprestimos;
