import React, { useState } from 'react';

function ControleDeEstoque() {
  const [selectedOption, setSelectedOption] = useState('');

  const handleChange = (event) => {
    setSelectedOption(event.target.value);
  };

  return (
    <div className=''>
      <select value={selectedOption} onChange={handleChange} placeholder='Selecione uma opção'>
        <option value="">Selecione uma opção</option>
        <option value="opcao1">Opção 1</option>
        <option value="opcao2">Opção 2</option>
        <option value="opcao3">Opção 3</option>
      </select>

      {selectedOption === 'opcao1' && <div>Conteúdo da opção 1</div>}
      {selectedOption === 'opcao2' && <div>Conteúdo da opção 2</div>}
      {selectedOption === 'opcao3' && <div>Conteúdo da opção 3</div>}
    </div>
  );
}

export default ControleDeEstoque;
