import "./Emprestimos.css";
import gif from "../Imagens/Imagens Site/Research paper.gif";
import React, { useState } from "react";

function Emprestimos() {
  const [number, setNumber] = useState(0);

  const incrementNumber = () => {
    if (number < 20) {
      setNumber(number + 1);
    }
  };

  const decrementNumber = () => {
    if (number > 0) {
      setNumber(number - 1);
    }
  };

  return (
    <div className="Emprestimos">
      <h1>Empréstimos de Equipamento</h1>
      <div id="Base-site">
        <div className="image-container">
          <img src={gif} id="Gif" />
        </div>

        <div className="number-button-container">
          <span>Lápis: </span>
          <button onClick={incrementNumber} disabled={number === 20} className="Button">
            +
          </button>
          <span>{number}</span>
          <button onClick={decrementNumber} disabled={number === 0} className="Button">
            -
          </button>
        </div>
        <div className="number-button-container">
          <span>Borracha: </span>
          <button onClick={incrementNumber} disabled={number === 20} className="Button">
            +
          </button>
          <span>{number}</span>
          <button onClick={decrementNumber} disabled={number === 0} className="Button">
            -
          </button>
        </div>
      </div>
    </div>
  );
}

export default Emprestimos;
